package com.bilin.classloader;public class Person {	private int age;	private String name;	public Person() {		System.out.println("构造 Person");	}	public Person(int age, String name) {		this.setAge(age);		this.setName(name);	}	public int getAge() {		return age;	}	public void setAge(int age) {		this.age = age;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public  static void test() {		System.out.println("static test Person");	}		public void test1() {		System.out.println("test1 Person");	}}